mapper.select:
SELECT ACCT_NO, ACCT_NM, BR_CODE FROM TB_ACCTINFO TA WHERE ACCT_NO NOT IN
#{item}
ORDER BY ACCT_NO

SELECT TB.BR_NM, TB.BR_CODE, SUM(AMT-FEE) AS SUM_AMT FROM ( SELECT TD.ACCT_NO, TD.AMT, TD.FEE, TA.BR_CODE FROM TB_DEDTLS TD LEFT JOIN TB_ACCTINFO TA ON TD.ACCT_NO = TA.ACCT_NO WHERE TD.CAN_AT='N' ) TDTA LEFT JOIN TB_BRINFO TB ON TDTA.BR_CODE = TB.BR_CODE
WHERE TB.BR_NM =#{acctNm}
GROUP BY TB.BR_NM , TB.BR_CODE
